package com.icst.commonmodule.utilsimport java.util.regex.Patternclass Validator {    private val EMAIL_PATTERN =        "^[_A-Za-z0-9-\\+]+(\\.[_A-Za-z0-9-]+)*@" + "[A-Za-z0-9-]+(\\.[A-Za-z0-9]+)*(\\.[A-Za-z]{2,})$"    private val PHONE_NUMBER="^\\+[0-9]{10,13}\$"    fun validateTextNotNull(email: String): Boolean {        return email.trim { it <= ' ' }.isNotEmpty()    }    fun validateEmail(email: String): Boolean {        val pattern = Pattern.compile(EMAIL_PATTERN)        val matcher = pattern.matcher(email)        return matcher.matches()    }    fun validatePhone(phone: String): Boolean {        val pattern = Pattern.compile(PHONE_NUMBER)        val matcher = pattern.matcher(phone)        return matcher.matches()    }    private val HIGHT_WIGHT =        "^\\d+(\\.\\d+)?\$"    public fun validateHightWight(email: String): Boolean {        val pattern = Pattern.compile(HIGHT_WIGHT)        val matcher = pattern.matcher(email)        return matcher.matches()    }}